mapper.select:
SELECT count(*) FROM pj_employ_detp       
    where 1=1                      
          AND ( detpname LIKE CONCAT('%',#{searchWord}, '%') OR detptitle LIKE CONCAT('%', #{searchWord}, '%') );

SELECT detpno,detpname, detptitle, detpexpiry, detpperiod, detptitleinfo FROM ( SELECT @RNUM := @RNUM + 1 AS r, a.* FROM ( SELECT a.detpno, a.detpname, a.detptitle, a.detpexpiry, a.detpperiod, a.detptitleinfo FROM pj_employ_detp a WHERE 1 = 1
and detpname = #{searchWord}
ORDER BY detpno DESC ) a, (SELECT @RNUM:=0) b ) temp WHERE r BETWEEN #{start} AND #{end}

mapper.update:
UPDATE pj_employ_detp   


         detptitle   = #{detptitle},   


        detpperiod   = #{detpperiod},  


        detptitleinfo  = #{detptitleinfo}


 WHERE  1 = 1 
      AND detpno=#{detpno}

mapper.delete:
DELETE FROM pj_employ_detp 
 WHERE 1 = 1 

  AND detpno  = #{detpno}
