mapper.select:
select userid, name, email, mobile, userno 
  from pj_user
   AND userid = #{userid}
   AND passwd = #{passwd}

SELECT count(*)
FROM pj_user
where userid = #{userid}

mapper.update:
UPDATE pj_user
email = #{setValue.email } , passwd = #{setValue.passwd } , name = #{setValue.name } , mobile = #{setValue.mobile } ,
WHERE 1 = 1
AND userno = #{whereValue.userno}
AND userid = #{whereValue.userid}

	        UPDATE pj_user 
	           SET passwd = #{newPasswd}            
	         WHERE userid = #{userid}
               AND passwd = #{currentPasswd}
