mapper.select:

SELECT boardnm
  FROM pj_training_bbs
 WHERE boardcd = #{boardcd}

SELECT boardcd, boardnm, UseYN 
  FROM pj_training_bbs 
 WHERE boardcd = #{boardcd}

SELECT boardcd, boardnm, UseYN FROM ( SELECT @RNUM := @RNUM + 1 AS r, a.* FROM ( SELECT boardcd, boardnm, UseYN FROM pj_training_bbs WHERE 1 = 1
AND boardnm LIKE CONCAT( '%' , #{searchWord} , '%' )
GROUP BY boardcd, boardnm, UseYN ORDER BY boardcd DESC ) a, (SELECT @RNUM:=0) b ) temp WHERE r BETWEEN #{start} AND #{end}

mapper.insert:
INSERT INTO pj_training_bbs( boardcd , boardnm , UseYN  )
VALUES

        #{item.boardcd} , #{item.boardnm} , #{item.UseYN}

mapper.update:
UPDATE pj_training_bbs

       boardnm   = #{setValue.boardnm}   , 
       UseYN     = #{setValue.UseYN}     , 
       UpdateUID = #{setValue.UpdateUID} , 
       UpdateDT  = #{setValue.UpdateDT}  , 

    WHERE 1 = 1 

      AND boardcd    = #{whereValue.boardcd}


      AND boardnm  = #{whereValue.boardnm}


      AND UseYN    = #{whereValue.UseYN}

mapper.delete:
DELETE FROM pj_training_bbs
 WHERE 1 = 1 

  AND boardcd  = #{boardcd}


  AND boardnm  = #{boardnm}


  AND UseYN    = #{UseYN}

mapper.select:
SELECT count(*) FROM pj_training 
 WHERE boardcd = #{boardcd}

    AND ( title LIKE CONCAT('%', #{searchWord}, '%') OR content LIKE CONCAT('%', #{searchWord}, '%') )

SELECT articleno, title, regdate, hit, attachfileNum, commentNum, name FROM ( SELECT @RNUM := @RNUM + 1 AS r, a.* FROM ( SELECT a.articleno articleno , a.title , a.regdate , a.hit , count(distinct(f.attachfileno)) attachfileNum , count(distinct(c.commentno)) commentNum , m.name FROM pj_training a LEFT OUTER JOIN pj_training_file f ON a.articleno = f.articleno LEFT OUTER JOIN pj_comments c ON a.articleno = c.articleno LEFT OUTER JOIN pj_user m ON a.insertUID = m.userid WHERE a.boardcd = #{boardcd}
AND (title LIKE CONCAT('%', #{searchWord}, '%') OR content LIKE CONCAT('%', #{searchWord}, '%') )
GROUP BY a.articleno, title, a.regdate, hit, m.name ORDER BY articleno DESC ) a, (SELECT @RNUM:=0) b ) temp WHERE r BETWEEN #{start} AND #{end}

SELECT articleno, boardcd, title, content, email, hit, regdate
FROM pj_training 
WHERE articleno = #{articleno}

mapper.update:
UPDATE pj_training   

         title     = #{setValue.title    } , 
         content   = #{setValue.content  } , 
         UseYN     = #{setValue.UseYN    } , 
         UpdateUID = #{setValue.UpdateUID} , 
         UpdateDT  = #{setValue.UpdateDT } , 

 WHERE  1 = 1 

      AND boardcd    = #{whereValue.boardcd}


      AND articleno  = #{whereValue.articleno}


      AND title      = #{whereValue.title}


      AND email      = #{whereValue.email}


      AND UseYN      = #{whereValue.UseYN}

mapper.delete:
DELETE FROM pj_training  
 WHERE 1 = 1 

  AND articleno = #{articleno}


  AND boardcd  = #{boardcd}


  AND email    = #{email}


  AND UseYN    = #{UseYN}

mapper.update:
UPDATE pj_training 
   SET hit=hit+1 
 WHERE articleno=#{articleno}

mapper.select:
SELECT articleno, title FROM ( SELECT @RNUM := @RNUM + 1 AS r, a.* FROM (SELECT articleno, title FROM pj_training WHERE boardcd = #{boardcd} AND articleno > #{articleno}
AND (title LIKE CONCAT('%', #{searchWord}, '%') OR content LIKE CONCAT('%', #{searchWord}, '%') )
ORDER BY articleno ASC ) a, (SELECT @RNUM:=0) b ) temp WHERE r = 1

SELECT articleno, title FROM ( SELECT @RNUM := @RNUM + 1 AS r, a.* FROM (SELECT articleno, title FROM pj_training WHERE boardcd = #{boardcd} AND articleno < #{articleno}
AND (title LIKE CONCAT('%', #{searchWord}, '%') OR content LIKE CONCAT('%', #{searchWord}, '%') )
ORDER BY articleno DESC ) a, (SELECT @RNUM:=0) b ) temp WHERE r = 1

SELECT attachfileno, filenameorig, filenametemp, filetype, filesize, articleno
  FROM pj_training_file
 WHERE attachfileno = #{attachFileNo}

