mapper.select:
	        select userid, name, address, email, mobile, userno, userclass
	          from pj_user
               WHERE retireYN=0
	           AND userid = #{userid}
	           AND passwd = #{passwd}

SELECT *
FROM pj_user
WHERE 1 = 1

    AND userid = #{userid}


    AND name = #{name}


    AND email = #{email}


    AND mobile = #{mobile}


    AND q_no = #{q_no}


    AND q_answer = #{q_answer}

SELECT count(*)
FROM pj_user
where userid = #{userid}

mapper.update:
UPDATE pj_user
email = #{setValue.email } , passwd = #{setValue.passwd } , name = #{setValue.name } , mobile = #{setValue.mobile } ,
WHERE 1 = 1
AND userno = #{whereValue.userno}
AND userid = #{whereValue.userid}

	        UPDATE pj_user 
	           SET passwd = #{newPasswd}            
	         WHERE userid = #{userid}
               AND passwd = #{currentPasswd}

	        UPDATE pj_user SET
            retireYN = 1 
	        WHERE userid = #{userid}
